import React, { useState, useEffect } from 'react';
import './EmailViewer.css';

const EmailViewer = () => {
  const [humanSortedEmails, setHumanSortedEmails] = useState([]);
  const [unsortedEmails, setUnsortedEmails] = useState([]);
  const [humanSortedIndex, setHumanSortedIndex] = useState(0);
  const [unsortedIndex, setUnsortedIndex] = useState(0);

  useEffect(() => {
    async function loadEmails(directory, setEmails) {
      try {
        const response = await fetch(`${process.env.PUBLIC_URL}/${directory}/index.json`);

        if (!response.ok) {
          throw new Error(`Error loading index.json from ${directory}`);
        }

        const filenames = await response.json();

        const emailPromises = filenames.map(async (filename) => {
          try {
            const emailResponse = await fetch(`${process.env.PUBLIC_URL}/${directory}/${filename}`);

            if (!emailResponse.ok) {
              throw new Error(`Error loading ${filename} from ${directory}`);
            }

            return await emailResponse.json();
          } catch (error) {
            console.error(error);
            return null;
          }
        });

        const loadedEmails = (await Promise.all(emailPromises)).filter(email => email !== null);
        setEmails(loadedEmails);
      } catch (error) {
        console.error(error);
      }
    }

    loadEmails('human_sorted', setHumanSortedEmails);
    loadEmails('unsorted', setUnsortedEmails);
  }, []);

  const renderEmail = (email) => {
    if (!email) return null;

    return (
      <>
        <h3>{email.email_subject}</h3>
        <p>From: {email.email_sender}</p>
        <p>Date: {email.email_date}</p>
        <pre>{email.prompt}</pre>
        <hr />
      </>
    );
  };

  const changeIndex = (currentIndex, emailsLength, changeFunc, increment) => {
    const newIndex = currentIndex + increment;
    if (newIndex >= 0 && newIndex < emailsLength) {
      changeFunc(newIndex);
    }
  };

  return (
    <div className="email-viewer-container">
      <div className="email-box human-sorted">
        <h2>Human Sorted Emails</h2>
        {renderEmail(humanSortedEmails[humanSortedIndex])}
        <button onClick={() => changeIndex(humanSortedIndex, humanSortedEmails.length, setHumanSortedIndex, -1)}>Previous</button>
        <button onClick={() => changeIndex(humanSortedIndex, humanSortedEmails.length, setHumanSortedIndex, 1)}>Next</button>
      </div>
      <div className="email-box unsorted">
        <h2>Unsorted Emails</h2>
        {renderEmail(unsortedEmails[unsortedIndex])}
        <button onClick={() => changeIndex(unsortedIndex, unsortedEmails.length, setUnsortedIndex, -1)}>Previous</button>
        <button onClick={() => changeIndex(unsortedIndex, unsortedEmails.length, setUnsortedIndex, 1)}>Next</button>
      </div>
    </div>
  );
};

export default EmailViewer;
