import React, { useState, useEffect } from 'react';
import './EmailViewer.css';

const EmailViewer = () => {
  const [humanSortedEmails, setHumanSortedEmails] = useState([]);
  const [unsortedEmails, setUnsortedEmails] = useState([]);
  const [humanSortedIndex, setHumanSortedIndex] = useState(0);
  const [unsortedIndex, setUnsortedIndex] = useState(0);
  const [rating, setRating] = useState('');
  const [classification, setClassification] = useState('');
  const [unsortedFilenames, setUnsortedFilenames] = useState([]);

  useEffect(() => {
    async function loadEmails(directory, setEmails) {
      try {
        const response = await fetch(`${process.env.PUBLIC_URL}/${directory}/index.json`);

        if (!response.ok) {
          throw new Error(`Error loading index.json from ${directory}`);
        }

        const filenames = await response.json();

        const emailPromises = filenames.map(async (filename) => {
          try {
            const emailResponse = await fetch(`${process.env.PUBLIC_URL}/${directory}/${filename}`);

            if (!emailResponse.ok) {
              throw new Error(`Error loading ${filename} from ${directory}`);
            }

            return await emailResponse.json();
          } catch (error) {
            console.error(error);
            return null;
          }
        });

        const loadedEmails = (await Promise.all(emailPromises)).filter(email => email !== null);
        setEmails(loadedEmails);
      } catch (error) {
        console.error(error);
      }
      const unsortedResponse = await fetch(`${process.env.PUBLIC_URL}/unsorted/index.json`);
      const unsortedFileNamesData = await unsortedResponse.json();
      setUnsortedFilenames(unsortedFileNamesData);

      // Load unsorted emails
      const unsortedEmailPromises = unsortedFileNamesData.map(async (filename) => {
        const emailResponse = await fetch(`${process.env.PUBLIC_URL}/unsorted/${filename}`);
        return await emailResponse.json();
      });

      const loadedUnsortedEmails = await Promise.all(unsortedEmailPromises);
      setUnsortedEmails(loadedUnsortedEmails);
    }
    
    loadEmails('human_sorted', setHumanSortedEmails);
    loadEmails('unsorted', setUnsortedEmails);
  }, []);
  
  const moveEmail = async (email, originalPath) => {
    const newPath = `human_sorted/${classification}_${rating}_${email.email_date}_${email.email_sender.replace(/[^a-zA-Z0-9]/g, '_')}.json`;

    try {
      const response = await fetch('/api/move-email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ originalPath, newPath }),
      });

      if (!response.ok) {
        throw new Error('Failed to move email.');
      }

      // Update unsortedEmails state to remove the moved email
      setUnsortedEmails((prevState) => prevState.filter((e) => e !== email));

      // Reset rating and classification inputs
      setRating('');
      setClassification('');

      alert('Email moved successfully.');
    } catch (error) {
      console.error(error);
      alert('Failed to move email.');
    }
  };

  const renderEmail = (email) => {
    if (!email) return null;

    return (
      <>
        <h3>{email.email_subject}</h3>
        <p>From: {email.email_sender}</p>
        <p>Date: {email.email_date}</p>
        <pre>{email.prompt}</pre>
        <hr />
      </>
    );
  };

  const changeIndex = (currentIndex, emailsLength, changeFunc, increment) => {
    const newIndex = currentIndex + increment;
    if (newIndex >= 0 && newIndex < emailsLength) {
      changeFunc(newIndex);
    }
  };

  return (
    <div className="email-viewer-container">
      <div className="email-box human-sorted">
        <h2>Human Sorted Emails</h2>
        {renderEmail(humanSortedEmails[humanSortedIndex])}
        <button onClick={() => changeIndex(humanSortedIndex, humanSortedEmails.length, setHumanSortedIndex, -1)}>Previous</button>
        <button onClick={() => changeIndex(humanSortedIndex, humanSortedEmails.length, setHumanSortedIndex, 1)}>Next</button>
      </div>
      <div className="email-box unsorted">
        <h2>Unsorted Emails</h2>
        {renderEmail(unsortedEmails[unsortedIndex])}
        <div>
          <label htmlFor="rating">Rating (1-10): </label>
          <input
            id="rating"
            type="number"
            min="1"
            max="10"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="classification">Classification: </label>
          <input
            id="classification"
            type="text"
            value={classification}
            onChange={(e) => setClassification(e.target.value)}
          />
        </div>
        <button
          onClick={() =>
            moveEmail(unsortedEmails[unsortedIndex], `unsorted/${unsortedFilenames[unsortedIndex]}`)
          }
        >
          Submit and Move Email
        </button>
        <button onClick={() => changeIndex(unsortedIndex, unsortedEmails.length, setUnsortedIndex, -1)}>
          Previous
        </button>
        <button onClick={() => changeIndex(unsortedIndex, unsortedEmails.length, setUnsortedIndex, 1)}>
          Next
        </button>
      </div>
    </div>
  );
};

export default EmailViewer;
