import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [emailFiles, setEmailFiles] = useState([]);
  const [fileIndex, setFileIndex] = useState(0);
  const [emailData, setEmailData] = useState({});
  const [rating, setRating] = useState('');
  const [classification, setClassification] = useState('');

  useEffect(() => {
    // Fetch email files
    axios.get('../../../human_sorted').then((response) => {
      setEmailFiles(response.data);
      loadNextEmail();
    });
  }, []);

  const loadNextEmail = () => {
    if (fileIndex >= emailFiles.length) {
      alert('All emails have been reviewed.');
      return;
    }

    const file = emailFiles[fileIndex];
    axios.get(`../../unsorted/${file}`).then((response) => {
      setEmailData(response.data);
      setRating(response.data.rating || '');
      setClassification(response.data.classification || '');
    });
  };

  const saveEmail = () => {
    if (!rating || !classification) {
      alert('Please enter both rating and classification.');
      return;
    }

    // Save the email data to the server
    axios
      .post('/path/to/your/save-email', {
        ...emailData,
        rating,
        classification,
      })
      .then(() => {
        setFileIndex(fileIndex + 1);
        loadNextEmail();
      });
  };

  return (
    <div className="App">
      <h1>Email Review</h1>
      <textarea
        value={emailData.prompt || ''}
        readOnly
        style={{ width: '100%', height: '300px' }}
      />
      <div>
        <label>Rating (1-9):</label>
        <input
          type="number"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
        />
      </div>
      <div>
        <label>Classification:</label>
        <input
          type="text"
          value={classification}
          onChange={(e) => setClassification(e.target.value)}
        />
      </div>
      <button onClick={saveEmail}>Save</button>
      <button onClick={loadNextEmail}>Next</button>
    </div>
  );
}

export default App;
